import collections


class Solution:
    def longestSubstring(self, s: str, k: int) -> int:
        n = len(s)
        for win_len in range(n, 0, -1):
            l, r = 0, win_len - 1
            count_map = collections.Counter(s[l : r + 1])
            uniq = len(count_map)

            while r < n:
                k_or_more_count = 0
                for freq in count_map.values():
                    if freq >= k: k_or_more_count += 1

                if k_or_more_count == uniq: return win_len

                count_map[s[l]] -= 1
                if count_map[s[l]] == 0:
                    del count_map[s[l]]
                    uniq -= 1

                l += 1; r += 1
                if r < n:
                    count_map[s[r]] += 1
                    if count_map[s[r]] == 1: uniq += 1

        return 0


sol = Solution()
assert sol.longestSubstring("bbaaacbd", 3) == 3
assert sol.longestSubstring("", 301) == 0
